import React, { MouseEventHandler } from 'react';

import { makeStyles } from '@mui/styles';

import { directionType } from '../../components/table-box';

interface ArrowIconProps {
    onClick?: MouseEventHandler;
    direction?: directionType;
    className?: string;
}

const useStyles = makeStyles({
    arrow: {
        marginLeft: 10,
        marginRight: 10,
    },

    arrow_up: {
        marginLeft: 10,
        marginRight: 10,
        transform: 'rotate(180deg)'
    },
})

function ArrowIcon({ onClick, direction, className }: ArrowIconProps): React.ReactElement {
    const classes = useStyles();

    return <svg className={direction === "asc" ? classes.arrow : classes.arrow_up} onClick={onClick} width="12" height="7" viewBox="0 0 12 7" fill="none">
        <line x1="0.353553" y1="0.646447" x2="6.18011" y2="6.47301" stroke="#FCFCFC" />
        <line x1="5.64645" y1="6.30331" x2="11.3033" y2="0.646453" stroke="white" />
    </svg>
}

export default ArrowIcon;
