import { createSlice } from '@reduxjs/toolkit';

const update = (state: any, mutations: any) =>
    Object.assign({}, state, mutations)

const tableDataSlice = createSlice({
    name: 'tableData',
    initialState: {
        isLoading: true,
        data: [],
        allData: [],
        pagination: {
            total: 0,
            count: 1,
        }
    },
    reducers: {
        getTableData(state, action) {
            state.allData = action.payload;
            state.data = action.payload.slice(0, 10);
            state.pagination.total = Math.ceil(action.payload.length / 10)
        },
        setPaginationCount(state, action) {
            state.pagination.count = action.payload;
            state.data = [...state.allData].slice((action.payload - 1) * 10, 10);
        },
        setLoading(state, action) {
            state.isLoading = action.payload;
        },
    }
})

export const { getTableData, setPaginationCount, setLoading } = tableDataSlice.actions
export default tableDataSlice.reducer
