import React from 'react';

import { TableCell as TableCellMui } from '@mui/material';
import { makeStyles } from '@mui/styles';

import { statusType } from '../../store/types';
import StatusBadge from '../status-badge';

const useStyles = makeStyles({
    table_row: {
        border: '1px solid #E3E6EC',
        maxWidth: '250px',

    },

    first_text: {
        whiteSpace: "nowrap",
        overflow: "hidden",
        textOverflow: "ellipsis",
    },

    secondary_text: {
        color: '#908f8f',
        whiteSpace: "nowrap",
        overflow: "hidden",
        textOverflow: "ellipsis"
    },

    status: {

    }
});

interface TableCellProps {
    text?: string | number;
    secondaryText?: string;
    status?: statusType;
}

function TableCell({ text, secondaryText, status }: TableCellProps): React.ReactElement {
    const classes = useStyles();


    return <TableCellMui className={classes.table_row}>
        {text && <p className={classes.first_text}>{text}</p>}
        {secondaryText && <p className={classes.secondary_text}>{secondaryText}</p>}
        {status && <StatusBadge status1={status} />}
    </TableCellMui>
}

export default TableCell
