import { createSlice } from '@reduxjs/toolkit';

const tableDataSlice = createSlice({
    name: 'tableData',
    initialState: {
        isLoading: true,
        data: [],
        allData: [],
        filterData: [],
        pagination: {
            total: 0,
            count: 1,
        }
    },
    reducers: {
        getTableData(state, action) {
            return {
                ...state,
                allData: action.payload,
                filterData: action.payload,
                data: action.payload.slice(0, 10),
                pagination: {
                    ...state.pagination,
                    total: Math.ceil(action.payload.length / 10),
                }
            }
        },
        setFilterData(state, action) {
            const startIndex = (action.payload - 1) * 10;
            return {
                ...state,
                data: state.filterData.slice(startIndex, startIndex + 10),
                pagination: {
                    ...state.pagination,
                    count: action.payload,
                }
            }
        },
        setPaginationCount(state, action) {
            const startIndex = (action.payload - 1) * 10;
            return {
                ...state,
                data: state.filterData.slice(startIndex, startIndex + 10),
                pagination: {
                    ...state.pagination,
                    count: action.payload,
                }
            }
        },
        setLoading(state, action) {
            return {
                ...state,
                isLoading: action.payload
            }
        },
    }
})

export const { getTableData, setPaginationCount, setLoading } = tableDataSlice.actions
export default tableDataSlice.reducer
