import React, { useEffect } from 'react';

import axios from 'axios';
import {
    useDispatch,
    useSelector
} from 'react-redux';

import {
    CircularProgress,
    Pagination,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    TableSortLabel
} from '@mui/material';
import { makeStyles } from '@mui/styles';

import {
    getTableData,
    setLoading
} from '../store/reducers/tableData';

const useStyles = makeStyles({
    container: {
        width: 1077,
        marginLeft: 78,
    },

    head: {
        backgroundColor: '#474955',
    },

    row: {
        borderStyle: 'none',
        background: '#474955',
    },

    head_row: {
        color: 'white',
        background: '#474955',
    },

    sort_label: {
        borderColor: 'white',
    },

    paggination: {
        display: 'flex',
        justifyContent: 'center',
    },
});


function Tables(): React.ReactElement {
    const classes = useStyles();

    const tableData = useSelector((state: any) => state.tableData.data)
    const isLoading = useSelector((state: any) => state.tableData.isLoading)
    const dispatch = useDispatch();

    useEffect(() => {
        const apiUrl = 'https://jsonplaceholder.typicode.com/posts';
        axios.get(apiUrl).then((resp: any) => {
            dispatch(getTableData(resp.data))
        });

        dispatch(setLoading(false));
    }, []);

    const filteredText = getTableData.filter(text => {
