    })

    return (
        <TableContainer className={classes.container}>
            <Table >
                <TableHead className={classes.head}>
                    <TableRow className={classes.row}>
                        <TableCell className={classes.head_row}>ID
                            <TableSortLabel className={classes.sort_label} />
                        </TableCell>
                        <TableCell>Заголовок
                            <TableSortLabel className={classes.sort_label} />
                        </TableCell>
                        <TableCell>Описание
                            <TableSortLabel className={classes.sort_label} />
                        </TableCell>
                    </TableRow>
                </TableHead>

                <TableBody>
                    {isLoading ? <CircularProgress /> :
                        tableData.map((data: any, i: number) => <TableRow key={data.id}>
                            <TableCell>{data.id}</TableCell>
                            <TableCell>{data.title}</TableCell>
                            <TableCell>{data.body}</TableCell>
                        </TableRow>)}
                </TableBody>

            </Table>
            <Pagination className={classes.paggination} />
        </TableContainer>
    );
}

export default Tables;
