import React from 'react';

import { makeStyles } from '@mui/styles';

import { statusType } from '../store/types';

const useStyles = makeStyles({
    status: {
        color: 'white',
        background: 'red',
        padding: '6px 10px',

    },
    new: {

    },
    assignedTo: {

    },
    completed: {

    },
    declined: {

    },
    started: {

    }
})


function StatusBadge({ status }: { status: statusType }): React.ReactElement {
    const classes = useStyles();

    switch (status) {
        case 'new':
            return <span className={`${classes.status} ${classes.new}`}>Новый</span>
        case 'assigned_to':
            return <span className={`${classes.status} ${classes.assignedTo}`}>Новый</span>
        case 'completed':
            return <span className={`${classes.status} ${classes.completed}`}>Новый</span>
        case 'declined':
            return <span className={`${classes.status} ${classes.declined}`}>Новый</span>
        case 'started':
            return <span className={`${classes.status} ${classes.started}`}>Новый</span>
        default:
            return <span className={classes.status}>{status}</span>
    }
}

export default StatusBadge;
