import React, {
    useEffect,
    useState
} from 'react';

import axios from 'axios';
import {
    useDispatch,
    useSelector
} from 'react-redux';

import {
    CircularProgress,
    Pagination,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow
} from '@mui/material';
import { makeStyles } from '@mui/styles';

import ArrowIcon from '../assets/icons/arrow';
import {
    getTableData,
    setLoading,
    setPaginationCount,
    sortData
} from '../store/reducers/tableData';
import { RootState } from '../store/store';

const useStyles = makeStyles({
    container: {
        width: 1077,
        marginLeft: 78,
    },

    head: {
        backgroundColor: '#474955',
        width: 1077,
    },

    row: {
        borderStyle: 'none',
        background: '#474955',

        '& .MuiTableCell-head': {
            color: 'white',
            background: '#474955',
            display: 'flex'
        },
    },

    table_row: {
        border: '1px solid #E3E6EC',
    },

    paggination: {
        display: 'flex',
        justifyContent: 'center',
    },
});

type columnName = 'id' | 'title' | 'description';

function Tables(): React.ReactElement {
    const [sortInfo, setSortInfo] = useState({ direction: 'asc', column: 'id' });
    const classes = useStyles();
    const dispatch = useDispatch();
    const tableData = useSelector((state: RootState) => state.tableData.data)
    const pagination = useSelector((state: RootState) => state.tableData.pagination)
    const isLoading = useSelector((state: RootState) => state.tableData.isLoading)

    useEffect(() => {
        const apiUrl = 'https://jsonplaceholder.typicode.com/posts';
        axios.get(apiUrl).then((resp: any) => {
            dispatch(getTableData(resp.data))
        });

        dispatch(setLoading(false));
    }, []);

    useEffect(() => {
        dispatch(sortData(sortInfo))
    }, [sortInfo]);

    const handleSortData = (column: columnName) => {
        if (sortInfo.column === column)
            setSortInfo((sort) => ({
                column,
                direction: sort.direction === 'asc' ? 'desc' : 'asc'
            }))
        else
            setSortInfo((sort) => ({
                column,
                direction: 'asc'
            }))
    }

    return (
        <>
            <button type="button" onClick={() => dispatch(sortData({ direction: 'desc', column: 'id' }))}>SortData</button>
            <TableContainer className={classes.container}>
                <Table>
                    <TableHead className={classes.head}>
                        <TableRow className={classes.row}>
                            <TableCell>
                                ID
                                <ArrowIcon
                                    direction={sortInfo.direction}
                                    onClick={() => handleSortData('id')}
                                />
                            </TableCell>
                            <TableCell>
                                Заголовок
                                <ArrowIcon
                                    direction={sortInfo.direction}
                                    onClick={() => handleSortData('title')}
                                />
                            </TableCell>
                            <TableCell>
                                Описание
                                <ArrowIcon
                                    direction={sortInfo.direction}
                                    onClick={() => handleSortData('description')}
                                />
                            </TableCell>
                        </TableRow>
                    </TableHead>

                    <TableBody>
                        {isLoading && <CircularProgress />}
                        {!isLoading && !tableData.length && <p>No data</p>}
                        {!isLoading && !!tableData.length &&
                            tableData.map((data: any) => <TableRow key={data.id}>
                                <TableCell align="center" className={classes.table_row}>{data.id}</TableCell>
                                <TableCell className={classes.table_row}>{data.title}</TableCell>
                                <TableCell className={classes.table_row}>{data.body}</TableCell>
                            </TableRow>)}
                    </TableBody>

                </Table>
                {!!tableData.length && <Pagination
                    className={classes.paggination}
                    count={pagination.total}
                    page={pagination.count}
                    onChange={(_, page) => dispatch(setPaginationCount(page))}
                />}
            </TableContainer>
        </>
    );
}

export default Tables;
