import React, { useEffect } from 'react';

import {
    useDispatch,
    useSelector
} from 'react-redux';

import {
    Pagination,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    TableSortLabel
} from '@mui/material';
import { makeStyles } from '@mui/styles';

import {
    getTableData,
    setLoading
} from '../store/reducers/tableData';

const useStyles = makeStyles({
    container: {
        width: 1077,
        marginLeft: 78,
    },

    head: {
        backgroundColor: '#474955',
    },

    row: {
        borderStyle: 'none',
        background: '#474955',
    },

    head_row: {
        color: 'white',
        background: '#474955',
    },

    sort_label: {
        borderColor: 'white',
    },

    paggination: {
        display: 'flex',
        justifyContent: 'center',
    },
});


function Tables(): React.ReactElement {
    const classes = useStyles();

    const tableData = useSelector((state: any) => state.tableData.data)
    const isLoading = useSelector((state: any) => state.tableData.isLoading)
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getTableData([{
            id: 1,
            title: 'foo',
            description: 'foo bar'
        }]))
        dispatch(setLoading(false));
    }, []);



    return (
        <TableContainer className={classes.container}>
            <Table >
                <TableHead className={classes.head}>
                    <TableRow className={classes.row}>
                        <TableCell className={classes.head_row}>ID
                            <TableSortLabel className={classes.sort_label} />
                        </TableCell>
                        <TableCell>Заголовок
                            <TableSortLabel className={classes.sort_label} />
                        </TableCell>
                        <TableCell>Описание
                            <TableSortLabel className={classes.sort_label} />
                        </TableCell>
                    </TableRow>
                </TableHead>
                {isLoading ? <p>...Loading</p> :
                    <TableBody>
                        {tableData.map((data: any) => <TableRow key={data.id}>
                            <TableCell>{data.id}</TableCell>
                            <TableCell>{data.title}</TableCell>
                            <TableCell>{data.description}</TableCell>
                        </TableRow>)}
                    </TableBody>
                }
            </Table>
            <Pagination className={classes.paggination} />
        </TableContainer>
    );
}

export default Tables;
