import React, {
  useEffect,
  useState,
} from 'react';

import axios from 'axios';
import {
  useDispatch,
  useSelector,
} from 'react-redux';
import {
  useNavigate,
  useParams,
} from 'react-router-dom';

import {
  Pagination,
  Table as TableMui,
  TableContainer,
} from '@mui/material';
import { makeStyles } from '@mui/styles';

import {
  getTableData,
  setLoading,
  setPaginationCount,
  sortData,
} from '../../store/reducers/tableData';
import { RootState } from '../../store/store';
import {
  sortInfoType,
  tableDataType,
} from '../../store/types';
import TableBody from './table-body';
import TableHead from './table-head';

const useStyles = makeStyles({
    container: {
        width: 1077,
        marginLeft: 78,
    },

    paggination: {
        display: 'flex',
        justifyContent: 'center',
    },
});

const API_URL = 'https://jsonplaceholder.typicode.com/posts';


function Table(): React.ReactElement {
    const navigate = useNavigate();
    const { page } = useParams();
    const classes = useStyles();
    const dispatch = useDispatch();

    const [sortInfo, setSortInfo] = useState<sortInfoType>({ direction: 'asc', column: 'id' });
    const pagination = useSelector((state: RootState) => state.tableData.pagination)

    useEffect(() => {
        axios.get(API_URL).then((resp: { data: tableDataType[] }) => {
            dispatch(getTableData(resp.data))
        }).then(() => {
            dispatch(setPaginationCount(Number(page)));
            dispatch(setLoading(false));
        });
    }, []);

    useEffect(() => {
        dispatch(setPaginationCount(Number(page)));
    }, [page]);

    useEffect(() => {
        dispatch(sortData(sortInfo))
    }, [sortInfo]);

    return (
        <TableContainer className={classes.container}>
            <TableMui>
                <TableHead setSortInfo={setSortInfo} sortInfo={sortInfo} />
                <TableBody />
            </TableMui>
            {!!pagination.total && <Pagination
                className={classes.paggination}
                count={pagination.total}
                page={pagination.count}
                onChange={(_, val) => navigate(`/${val}`)}
            />}
        </TableContainer>
    );
}

export default Table;
